<map version="1.0.1"><node TEXT="社会运行的物质基础" FOLDED="false"><edge COLOR="#BBBBBB"/><node TEXT="人口与社会运行" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="人口：即是指特定时间、特定地域所存在的人的集合，这样一种集合呈现出一定的数量、质量和构成特征，并由此对社会运行和发展产生基础性影响" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="人口规模和人口结构" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="人口规模" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="人口规模是指一定时间和地域内的人口总数" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="最小人口规模是指在一定的地域内，实现社会生产和维持集体生存所要求的最小人口数" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="最大人口规模是指在一定地域内，由社会生产、土地资源和环境承载力等因素所决定的最大人口数" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="适度人口规模：未达到则表现为人口不足及相关社会问题，超过则表达为人口过剩及相关社会问题" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="马尔萨斯主义，人口按几何级数增长，描绘人口增长超过生活资料增长的悲观前景，提倡节制人口" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="西蒙《没有极限的增长》" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="人口规模的影响因素" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="物质资料的生产等社会经济因素通过影响生育、死亡和迁移而影响人口规模" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="人口自然增长：出生人数和死亡人数之间的差额" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="正为正增长" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="负为负增长" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="人口迁移增长：迁入人口数和迁出人口数的差额" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="总出生率：平均每千人年出生人口数" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="一般生育率：一年里每一千个育龄妇女所生育的活婴数量" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="总和生育率：15-49岁各年龄组妇女生育率之和" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="死亡率：一年里平均每千人的死亡人数" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="净迁移率：特定地区一年里净迁移人口与平均总人口之比" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="净迁移人口：迁入人口和迁出人口的差额" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="人口增长率：在一定时期里人口增长数与人口总数之比。" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="一定时期一般是一年" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="人口增长数是年末人口数减去年初人口数" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="人口总数一般用年平均人口或年中人口" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="人口倍增时间：人口总数翻一番所花的时间" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="每增加10亿人口所需要的时间" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node></node><node TEXT="人口结构分析" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="人口的自然构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="性别构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="性别比：以女性人口数为100去计算男性人口数的半分比" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="高于100，偏高，男性人口多于女性人口" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="低于100，偏低，男性人口少于女性人口" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="年龄构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="60岁及以上人口占到10%或65%及以上人口占到7%以上，表示人口结构进入老年型" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="人口的地域构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="自然环境构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="行政区域构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="城乡构成" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="城市人口不断增多反应着现代社会的城市化进程" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="人口的社会经济构成" FOLDED="false" POSITION="righ